import { useEffect, useRef } from "react";
import "./App.css";

function App() {
	const ref = useRef<HTMLInputElement>(null);
	useEffect(() => {
		if (ref.current) ref.current.focus();
	});
	return (
		<div className="App">
			<input ref={ref} type="text"></input>
		</div>
	);
}

export default App;
